DROP TABLE `class`;
CREATE TABLE `class` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `schoolID` int(11) DEFAULT NULL,  `name` varchar(45) DEFAULT NULL,  `graduationYear` int(11) DEFAULT NULL,  `text` varchar(145) DEFAULT NULL,  `headTeacherID` int(11) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=96 DEFAULT CHARSET=utf8 COMMENT='School class';
DROP TABLE `interpret`;
CREATE TABLE `interpret` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(245) DEFAULT NULL,  `link` varchar(245) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=3406 DEFAULT CHARSET=utf8 COMMENT='Song interprets';
DROP TABLE `message`;
CREATE TABLE `message` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(145) DEFAULT NULL,  `text` text,  `comment` text,  `privacy` varchar(45) DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  `isDeleted` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=156 DEFAULT CHARSET=utf8 COMMENT='User messages';
DROP TABLE `person`;
CREATE TABLE `person` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `classID` int(11) NOT NULL,  `isTeacher` int(11) NOT NULL DEFAULT '0',  `firstname` varchar(245) NOT NULL,  `lastname` varchar(245) DEFAULT NULL,  `picture` varchar(245) DEFAULT NULL,  `geolat` varchar(45) DEFAULT NULL,  `geolng` varchar(45) DEFAULT NULL,  `user` varchar(145) NOT NULL,  `passw` varchar(255) DEFAULT NULL,  `role` varchar(145) DEFAULT NULL,  `birthname` varchar(145) DEFAULT NULL,  `partner` varchar(145) DEFAULT NULL,  `address` varchar(245) DEFAULT NULL,  `zipcode` varchar(45) DEFAULT NULL,  `place` varchar(145) DEFAULT NULL,  `country` varchar(145) DEFAULT NULL,  `phone` varchar(145) DEFAULT NULL,  `mobil` varchar(145) DEFAULT NULL,  `email` varchar(245) DEFAULT NULL,  `homepage` varchar(255) DEFAULT NULL,  `skype` varchar(145) DEFAULT NULL,  `education` varchar(245) DEFAULT NULL,  `employer` varchar(255) DEFAULT NULL,  `function` varchar(245) DEFAULT NULL,  `children` varchar(245) DEFAULT NULL,  `facebook` varchar(245) DEFAULT NULL,  `facebookid` varchar(45) DEFAULT NULL,  `twitter` varchar(245) DEFAULT NULL,  `cv` text,  `story` text,  `aboutMe` text,  `changeIP` varchar(45) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=918 DEFAULT CHARSET=utf8 COMMENT='Classmates und teachers';
DROP TABLE `picture`;
CREATE TABLE `picture` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `personID` int(11) DEFAULT NULL,  `schoolID` int(11) DEFAULT NULL,  `classID` int(11) DEFAULT NULL,  `file` varchar(255) DEFAULT NULL,  `isVisibleForAll` int(11) DEFAULT NULL,  `title` varchar(145) DEFAULT NULL,  `comment` varchar(8196) DEFAULT NULL,  `isDeleted` int(11) DEFAULT NULL,  `uploadDate` datetime DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=86 DEFAULT CHARSET=utf8;
DROP TABLE `request`;
CREATE TABLE `request` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `ip` varchar(45) DEFAULT NULL,  `typeID` int(11) DEFAULT NULL,  `date` datetime DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=192 DEFAULT CHARSET=utf8;
DROP TABLE `school`;
CREATE TABLE `school` (  `id` int(11) NOT NULL,  `name` varchar(145) DEFAULT NULL,  `nameGiver` text,  `history` text,  `phone` varchar(45) DEFAULT NULL,  `mail` varchar(145) DEFAULT NULL,  `homepage` varchar(245) DEFAULT NULL,  `addressCity` varchar(145) DEFAULT NULL,  `addressCountry` varchar(145) DEFAULT NULL,  `addressZipCode` varchar(45) DEFAULT NULL,  `addressStreet` varchar(45) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='School';
DROP TABLE `song`;
CREATE TABLE `song` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `interpretID` int(11) DEFAULT NULL,  `name` varchar(245) DEFAULT NULL,  `video` varchar(245) DEFAULT NULL,  `link` varchar(245) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=3053 DEFAULT CHARSET=utf8 COMMENT='Songs';
DROP TABLE `songvote`;
CREATE TABLE `songvote` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `personID` int(11) DEFAULT NULL,  `songID` int(11) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=7416 DEFAULT CHARSET=utf8 COMMENT='Song Votes';
DROP TABLE `vote`;
CREATE TABLE `vote` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `classID` int(11) DEFAULT NULL,  `personID` int(11) DEFAULT NULL,  `meetAfterYear` int(11) DEFAULT NULL,  `isSchool` int(11) DEFAULT NULL,  `isCemetery` int(11) DEFAULT NULL,  `isDinner` int(11) DEFAULT NULL,  `isExcursion` int(11) DEFAULT NULL,  `place` varchar(255) DEFAULT NULL,  `eventDay` varchar(255) DEFAULT NULL,  `changeDate` datetime DEFAULT NULL,  `changeIP` varchar(45) DEFAULT NULL,  `changeUserID` int(11) DEFAULT NULL,  `changeForID` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=utf8 COMMENT='Voting	';
